import React, { useState } from 'react';
import { FaRegCalendar } from "react-icons/fa";
import { TfiWrite } from "react-icons/tfi";

const ExpensesPage = ({ transactions, addTransaction }) => {

    const [expenseAmount, setExpenseAmount] = useState('');
    const [Expdate, setExpDate] = useState("");
  const [Expdesc, setExpDesc] = useState("");

    const handleAddExpense = (e) => {
        e.preventDefault();
        if (expenseAmount !== '') {
            const newExpense = {  id:Math.round(Math.random()*999999),date:Expdate,desc:Expdesc,type: "income", amount: parseFloat(expenseAmount) };
            addTransaction(newExpense);
            setExpenseAmount('');
            setExpDate("");
            setExpDesc("");
        }
    };

    const totalExpenses = transactions
        .filter((transaction) => transaction.type === 'expense')
        .reduce((total, transaction) => total + transaction.amount, 0);

    return (
        <>
        {/* <div>
            <h2>Expenses Page</h2>
            <form onSubmit={handleAddExpense}>
                <label>Expense Amount:</label>
                <input
                    type="number"
                    value={expenseAmount}
                    onChange={(e) => setExpenseAmount(e.target.value)}
                />
                <button type="submit">Add Expense</button>
            </form>
            <h3 className='history'>Expenses History</h3>
            <ul className='trackerList'>
                {transactions.map((transaction, index) => (
                    transaction.type === 'expense' && (
                        <li key={index}>
                            -{transaction.amount} TL
                        </li>
                    )
                ))}
            </ul>
            <h3>Total Expenses: ${totalExpenses}</h3>
        </div> */}


        <div className="page-container">
    <div className="form">
      <h1>Gider Sayfası</h1>
      <form onSubmit={handleAddExpense}>
        <input className="input"
        required
        placeholder="Gider miktarını giriniz.."
          type="number"
          value={expenseAmount}
          onChange={(e) => setExpenseAmount(e.target.value)}
        />
        <input className="input"
        placeholder="Açıklama giriniz.."
        required
        type="text" 
        value={Expdesc}
         onChange={(e) => setExpDesc(e.target.value)} 
         />
        <input className="input"
        
        required
          type="date"
          placeholder="Tarih giriniz.."
          value={Expdate}
          onChange={(e) => setExpDate(e.target.value)}
        />
        <button className="income-btn" type="submit"  >Gider Ekle</button>
      </form>
      </div>
      <div className="list">
      <h3 className="history">Eklenen gelirler</h3>
      {  <ul className='trackerList'>
                {transactions.map((transaction, index) => (
                    transaction.type === 'expense' && (
                        <li key={index} className='trackerlist-li'>
                            <p>-{transaction.amount} TL</p>   <p> <TfiWrite />{transaction.desc}</p> <p> <FaRegCalendar />{ transaction.date}  {transaction.id} </p>
                            
                        </li>
                        
                    )
                ))}
            </ul>}
            </div> 
      <h3>Total Income: ${totalExpenses}</h3>

      
      </div>
        </>
    );
};

export default ExpensesPage;
