import React, { useState } from 'react';

const IncomePage = ({ transactions, addTransaction}) => {
    const [incomeAmount, setIncomeAmount] = useState('');
    const [incomeDate, setIncomeDate] = useState('');
    const [incomeTitle, setIncomeTitle] = useState('');

    const handleAddIncome = (e) => {
        e.preventDefault();
        if (incomeAmount !== '') {
            const newIncome = { type: 'income', amount: parseFloat(incomeAmount) };
            addTransaction(newIncome);
            setIncomeAmount('');
        }
        if(incomeTitle !== ''){
            const newTitle = {  amount: parseFloat(incomeTitle) };
            addTransaction(Title);
            setIncome('');
        }
    };
    

    const totalIncome = transactions
        .filter((transaction) => transaction.type === 'income')
        .reduce((total, transaction) => total + transaction.amount, 0);

    const handleDelete = (index) =>{
        console.log(index)

    }

    return (
        <div>
            <h2>Income Page</h2>
            <form onSubmit={handleAddIncome}>
            <label>Başlık:</label>
                <input
                    type="text"
                    value={incomeTitle}
                    onChange={(e) => setIncomeTitle(e.target.value)}
                />
                <label>Income Amount:</label>
                <input
                    type="number"
                    value={incomeAmount}
                    onChange={(e) => setIncomeAmount(e.target.value)}
                />
                <label>Tarih:</label>
                <input 
                type="text"
                placeholder="__/__/____"
                value={incomeDate}
                onChange={(e) => setIncomeDate(e.target.value)}/>
                
                <button type="submit">Add Income</button>
            </form>
            <h3 className='history'>Income History</h3>
            <ul className='trackerList'>
                {transactions.map((transaction, index) => (
                    transaction.type === 'income' && (
                        <li key={index}>
                            +{transaction.amount} TL
                            <button className='btn-delete'onClick={handleDelete}>Sil</button>
                            <button className='btn-edit' >Güncelle</button>
                        </li>
                        
                    )
                ))}
            </ul>
            <h3>Total Income: ${totalIncome}</h3>
        </div>
    );
};

export default IncomePage;
