import {  Route, Routes } from 'react-router-dom';
import ExpensesPage from './components/ExpensesPage';
import Header from './components/Header';
import Home from './components/Home';
import IncomePage from './components/IncomePage';

const App = () => {
  const [transactions, setTransactions] = useState([]);
  // Function to add a new transaction
  const addTransaction = (newTransaction) => {
      const createdTransactions =[
        ...transactions,
        {
          id:Math.round(Math.random()*999999),
          date: newTransaction.date,
          desc: newTransaction.desc,
          type : newTransaction.type,
          amount:newTransaction.amount,
        },
      ];
      console.log(transactions)
      setTransactions(createdTransactions);


    // setTransactions([...transactions, newTransaction]);
    // console.log(newTransaction.title);

  };

  const onDelete =(id) =>{
    console.log(transactions.id);
  }




  return (

      <div className="app">
        <Header />
        <div className="container">
          <Routes>
            <Route path="/" element={<Home transactions={transactions} addTransaction={addTransaction} />} />
            <Route path="/income" element={<IncomePage transactions={transactions} addTransaction={addTransaction} onDelete={onDelete}/>} />
            <Route path="/expenses" element={<ExpensesPage transactions={transactions} addTransaction={addTransaction} />} />
          </Routes>
        </div>
      </div>

  );
};

export default App;
