import React, { useState } from "react";
import IncomePageList from "./IncomePageList";

const IncomePage = ({ transactions, addTransaction,onCreate }) => {
  const [incomeAmount, setIncomeAmount] = useState("");
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");

  const handleAddIncome = (e) => {
    e.preventDefault();
    if (incomeAmount !== "") {
      const newIncome = { type: "income", amount: parseFloat(incomeAmount) };
      addTransaction(newIncome);
      setIncomeAmount("");
    }
  };
  const handleSubmit =(e) =>{
    e.preventDefault();
    onCreate(title,desc);
  }

  const totalIncome = transactions
    .filter((transaction) => transaction.type === "income")
    .reduce((total, transaction) => total + transaction.amount, 0);

  return (
    <div>
      <h2>Income Page</h2>
      <form onSubmit={handleAddIncome}>
        <label>Income Amount:</label>
        <input
          type="number"
          value={incomeAmount}
          onChange={(e) => setIncomeAmount(e.target.value)}
        />
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
        type="text" 
        value={desc}
         onChange={(e) => setDesc(e.target.value)} />
        <button type="submit" onClick={handleSubmit} >Add Income</button>
      </form>
      <h3 className="history">Income History</h3>
      {/* <ul className='trackerList'>
                {transactions.map((transaction, index) => (
                    transaction.type === 'income' && (
                        <li key={index}>
                            +{transaction.amount} TL
                            <button className='btn-delete'onClick={handleDelete}>Sil</button>
                            <button className='btn-edit' >GÃ¼ncelle</button>
                        </li>
                        
                    )
                ))}
            </ul> */}
      <IncomePageList></IncomePageList>
      <h3>Total Income: ${totalIncome}</h3>
      <IncomePageList></IncomePageList>
    </div>
  );
};

export default IncomePage;
