import React, { useState } from "react";
import { FaEdit } from "react-icons/fa";
import { FaTrash } from "react-icons/fa";
import { FaRegCalendar } from "react-icons/fa";

import { TfiWrite } from "react-icons/tfi";



const IncomePage = ({ transactions, addTransaction,id,type,amount }) => {
  const [incomeAmount, setIncomeAmount] = useState("");
  const [date, setDate] = useState("");
  const [desc, setDesc] = useState("");
  

  const handleAddIncome = (e) => {
    e.preventDefault();
    if (incomeAmount !== "" ) {
      
      addTransaction(date,desc,type='income', amount=parseFloat(incomeAmount));
      setIncomeAmount("");
      setDate("");
      setDesc("");
    }
  };
  const handleDelete =()=>{
    onDelete(transaction.id)
  }

  const totalIncome = transactions
    .filter((transaction) => transaction.type === "income")
    .reduce((total, transaction) => total + transaction.amount, 0);



  return (
    <div className="page-container">
    <div className="form">
      <h1>Gelir Sayfası</h1>
      <form onSubmit={handleAddIncome}>
        <input className="input"
        required
        placeholder="Gelir miktarını giriniz.."
          type="number"
          value={incomeAmount}
          onChange={(e) => setIncomeAmount(e.target.value)}
        />
        <input className="input"
        placeholder="Açıklama giriniz.."
        required
        type="text" 
        value={desc}
         onChange={(e) => setDesc(e.target.value)} 
         />
        <input className="input"
        
        required
          type="date"
          placeholder="Tarih giriniz.."
          value={date}
          onChange={(e) => setDate(e.target.value)}
        />
        <button className="income-btn" type="submit"  >Gelir Ekle</button>
      </form>
      </div>
      <div className="list">
      <h3 className="history">Eklenen gelirler</h3>
      {  <ul className='trackerList'>
                {transactions.map((transaction, index) => {
                    transaction.type === 'income' && (
                        <li key={index} className='trackerlist-li'>
                            <p>+{transaction.amount} TL</p>   <p> <TfiWrite />{transaction.desc}</p> <p> <FaRegCalendar />{ transaction.date}  {transaction.id} </p>
                            <div className="delete-edit">
                             <FaTrash className='btn-delete' onClick={handleDelete} />
                              <FaEdit className='btn-edit'/>
                              </div>
                        </li>
                        
                    )
                    })}
            </ul>}
            </div> 
      <h3>Total Income: ${totalIncome}</h3>
      </div>
    
  );
};

export default IncomePage;
