import React, { useState } from "react";

const IncomePage = ({ transactions, addTransaction, }) => {
  const [incomeAmount, setIncomeAmount] = useState("");
  const [date, setDate] = useState("");
  const [desc, setDesc] = useState("");

  const handleAddIncome = (e) => {
    e.preventDefault();
    if (incomeAmount !== "" &&
    date !== "" &&
    desc  !== "" ) {
      const newIncome = { type: "income", amount: parseFloat(incomeAmount),desc:desc,date:date};
      addTransaction(newIncome);
      setIncomeAmount("");
      setDate("");
      setDesc("");
    }
  };
  

  const totalIncome = transactions
    .filter((transaction) => transaction.type === "income")
    .reduce((total, transaction) => total + transaction.amount, 0);

  return (
    <div className="form">
      <h2>Income Page</h2>
      <form onSubmit={handleAddIncome}>
        <label>Income Amount:</label>
        <input className="input"
        required
        placeholder="Gelir miktarını giriniz.."
          type="number"
          value={incomeAmount}
          onChange={(e) => setIncomeAmount(e.target.value)}
        />
        <label>Açıklama</label>
        <input className="input"
        required
        type="text" 
        value={desc}
         onChange={(e) => setDesc(e.target.value)} 
         />
         <label>Tarih</label>
        <input className="input"
        
        required
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
        />
        <button className="Income-btn" type="submit"  >Add Income</button>
      </form>
      <div>
      <h3 className="history">Income History</h3>
      {  <ul className='trackerList'>
                {transactions.map((transaction, index) => (
                    transaction.type === 'income' && (
                        <li key={index}>
                            +{transaction.amount} TL  Kategori:{transaction.desc} Tarih {transaction.date} 
                            <button className='btn-delete'>Sil</button>
                            <button className='btn-edit' >Güncelle</button>
                        </li>
                        
                    )
                ))}
            </ul>}
            </div> 
      <h3>Total Income: ${totalIncome}</h3>
      
    </div>
  );
};

export default IncomePage;
